opam-version: "1.2"
maintainer: "unixjunkie@sdf.org"
authors: "Francois Berenger"
license: "GPL"
homepage: "http://www.freedesktop.org/wiki/Software/pkg-config/"
bug-reports: "https://github.com/ocaml/opam-repository/issues"
build: ["pkg-config" "--help"]
install:
  ["ln" "-s" "/usr/local/bin/pkgconf" "%{bin}%/pkg-config"] {os = "openbsd"}
remove: ["rm" "-f" "%{bin}%/pkg-config"] {os = "openbsd"}
post-messages:
  "conf-pkg-config: A symlink to /usr/local/bin/pkgconf has been installed in the OPAM bin directory (%{bin}%) on your PATH as 'pkg-config'. This is necessary for correct operation."
    {os = "openbsd"}
depexts: [
  ["pkg-config"] {"debian"}
  ["pkg-config"] {"ubuntu"}
  ["pkg-config"] {"archlinux"}
  ["pkgconfig"] {"fedora"}
  ["pkgconfig"] {"centos"}
  ["pkgconfig"] {"mageia"}
  ["pkgconfig"] {"rhel"}
  ["pkgconfig"] {"oraclelinux"}
  ["pkgconfig"] {"alpine"}
  ["devel/pkgconf"] {"freebsd"}
  ["devel/pkgconf"] {"openbsd"}
  ["pkg-config"] {"osx" & "homebrew"}
  ["pkgconf"] {"freebsd"}
]
dev-repo: "git://github.com/ocaml/opam-repository"

